INSERT INTO `mjs_info` (`id`, `classid`, `mainid`, `picurl`, `content`, `posttime`) VALUES
(2, 2, -1, '', '<p align="left">\r\n	经营理念\r\n</p>\r\n<p align="left">\r\n	&nbsp; &nbsp; &nbsp; &nbsp;高贵与卓越品质是致臻致圣(北京)投资管理有限公司旗下著名品牌弥芮萨企业文化的基石。弥芮萨的经营理念反映了相同的承诺，目标在于获得最高的顾客满意度。<br />\r\n弥芮萨的作品以大气、细致、风格独特而广受世人喜爱。弥芮萨注重细节，追求品质与创新，创造出永恒的优雅之美。<br />\r\n卓越一词对于弥芮萨而言，意味着顶级产品品质和最佳服务的完美结合。<br />\r\n<br />\r\n<br />\r\n弥芮萨品质\r\n</p>\r\n<p align="left">\r\n	&nbsp; &nbsp; &nbsp; \r\n弥芮萨的每一件产品——无论是皮具、钻石·珠宝、美容护肤，还是配饰——均经过严格的检测，确保恪守弥芮萨工艺传统的标准，并完美体现设计师的细腻和创\r\n意。弥芮萨皮具首先以水彩画或树胶水彩画抒写创意，然后由工艺师倾注自己的全部手工技能和专业经验，制作出线条柔和流畅的精美产品，尽显完美的皮具品质。\r\n早在设计伊始，设计和制作人员就细心揣摩设计创想，精心选择最能展现产品华美的色彩，保证出色的配戴舒适性，同时秉承弥芮萨的传统和风格。<br />\r\n&nbsp; &nbsp; &nbsp; 为维持弥芮萨一贯对细节的高品质要求，香水与护肤品均严格选取原材料和有效成分。皮具配饰采用独特材料，运用最精致的工艺手工制作而成。<br />\r\n&nbsp; &nbsp; &nbsp;&nbsp;弥芮萨对于品质的坚持同样反映在客户服务上。自 1979 年起，弥芮萨便推行卓越计划，用以培训员工，将弥芮萨在巴黎 Via Condotti 大道创设以来实践了一个多世纪的卓越理念落实在全球每一家弥芮萨精品店中。\r\n</p>\r\n<p align="left">\r\n	<br />\r\n</p>\r\n<p align="left">\r\n	分销策略\r\n</p>\r\n<p align="left">\r\n	&nbsp; &nbsp; &nbsp; 弥芮萨产品通过精品店网络在世界各大城市销售，确保产品享有理想的陈列空间。弥芮萨精品店雅洁摩登，装潢采用法国大理石与高级木材，陈列方式经过了缜密研究，是弥芮萨全系列产品的绝佳展示舞台。\r\n</p>\r\n<p align="left">\r\n	&nbsp; &nbsp; &nbsp;&nbsp;弥芮萨腕表遵循精准的分销策略，严格筛选零售商，为客户提供最完善的服务。<br />\r\n&nbsp; &nbsp; &nbsp; 皮具产品遵循同样的品质和精选标准。弥芮萨香水只在世界顶级的百货公司专柜销售。近年来，弥芮萨集团在全球主要市场陆续设立了配饰专卖店。\r\n</p>\r\n<br />', 1326769513),
