INSERT INTO `mjs_infolist` (`id`, `siteid`, `classid`, `parentid`, `parentstr`, `mainid`, `mainpid`, `mainpstr`, `title`, `colorval`, `boldval`, `flag`, `source`, `author`, `linkurl`, `keywords`, `description`, `content`, `picurl`, `picarr`, `hits`, `orderid`, `posttime`, `checkinfo`, `delstate`, `deltime`) VALUES
(25, 1, 9, 0, '0,', -1, -1, '', '法国高端时尚品牌“弥芮萨”正式入住中国', '', '', '', '环球臻品网', 'admin', '', '', '2013年8月20日，“臻善圣洁，博爱中国”——臻品互融创新共赢模式启动暨法国臻圣时尚品牌弥芮萨入住中国新闻发布会在北京九华山庄贵宾楼隆重召开，与会现场，200多位嘉宾一起见证了弥芮萨法国时尚。', '<p style="text-indent:2em;">\r\n	2013年8月20日，“臻善圣洁，博爱中国”——臻品互融创新共赢模式启动暨法国臻圣时尚品牌弥芮萨入住中国新闻发布会在北京九华山庄贵宾楼隆重召开，与会现场，200多位嘉宾一起见证了弥芮萨法国时尚。\r\n</p>\r\n<div style="text-indent:2em;">\r\n	<img title="dfad" alt="dfad" src="http://hunan.sinaimg.cn/2013/0822/U9765P1192DT20130822145405.jpg" /> \r\n</div>\r\n<p style="text-indent:2em;">\r\n	“弥芮萨”国际驰名品牌，臻善圣洁，古典高雅，精美别致，奢华脱俗。\r\n</p>\r\n<p style="text-indent:2em;">\r\n	致臻致圣(北京)投资管理有限公司是法国臻圣时尚产业集团在亚太地区唯一的投资、运营与管理商，管理运营旗下(MAIS JE \r\nSAN)弥芮萨品牌及关联企业。“弥芮萨”透着一股强劲的国际风扑面而来，工艺精美的皮具与林林总总的箱包，款式及质地独特的服装，现代商务高档、智能、\r\n三防手机等琳琅满目。令人真切地感到中国的是世界的，世界的是中国的。\r\n</p>\r\n<p style="text-indent:2em;">\r\n	致臻致圣公司温继华董事长具有超凡的才华和魄力，对中国市场有敏锐而独到的分析与掌控，2012年，斥资将法国MAIS JE \r\nSAN品牌引入中国。“弥芮萨”富有传奇色彩、引领时尚，更加体现了“让中国的时尚和世界没有距离”。更加吸引了无数时尚人群的极大关注和喜爱。时至今\r\n日，“弥芮萨”时尚品牌已经拥有高级男女时装，皮具、珠宝饰品、化妆品等四大产业精品。一个品牌的成功，从来都未离开过回报社会，钟情于慈善事业。“弥芮\r\n萨”已成为代表时尚界以商养慈的象征。使之得到品位高雅、时尚、尊贵的社会名流的青睐。“弥芮萨”经历了岁月的考验而成为了一个永恒的生活艺术和臻善圣洁\r\n的标志。\r\n</p>\r\n<div>\r\n	<img title="gsg" alt="gsg" src="http://hunan.sinaimg.cn/2013/0822/U9765P1192DT20130822145233.png" /> \r\n</div>\r\n<p style="text-indent:2em;">\r\n	致臻致圣公司经过一年的精心筹备，今天，在中国隆重地拉开帷幕并推向市场。\r\n</p>\r\n<p style="text-indent:2em;">\r\n	致臻致圣公司董事长温继华先生，他坚信帮助别人就等于帮助自己。公司的创新经营理念是：以慈善事业为己任，在企业发展过程中，每个店铺至少安排一名残\r\n疾人就业，待遇从优，充分使残疾朋友们体会到社会的关爱、人间的温暖！公司将拿出未来商业收益的5%，捐助国家的公益事业，为国家的经济发展，民生建设锦\r\n上添花。致臻致圣公司极具创新精神：采用国际最新的5M经营管理模式：1、高端会所服务延伸模式&nbsp;Extended&nbsp;service&nbsp;model；2、会\r\n员管家式服务模式butler&nbsp;service&nbsp;model；3、会所加盟的营销模式&nbsp;club&nbsp;marketing&nbsp;model；4、资源共享模式\r\nresource&nbsp;sharing&nbsp;model;&nbsp;5、品牌自我保护性运营模式&nbsp;&nbsp;Brand&nbsp;protection&nbsp;operation&nbsp;model。\r\n从而使“弥芮萨”品牌在中国发展壮大，让更多的青年就业，减轻政府就业压力，致臻致圣公司将在全新模式下全力经营，企业获得利益的同时，不忘报国。同时使\r\n这一国际品牌“弥芮萨”红遍大江南北。 \r\n“用创新赢得尊严。”我们坚信未来世界，谁能满足消费者更多的需求，谁的成本更低，速度更快，谁就一定能够赢得未来！\r\n</p>', 'uploads/image/20150619/1434704127.png', '', 156, 25, 1434636310, 'true', '', 0),
(24, 1, 9, 0, '0,', -1, -1, '', '公告：《环球臻品网》正式上线了', '', '', '', '环球臻品网', 'admin', '', '', '环球臻品网站是针对国内高人气网络消费人群创立的高质量、大品牌、上档次的综合型产品购物网站。销售的产品包括：服装鞋帽、礼品箱包、家具家装、办公 数码、电器珠宝、化妆品、食品饮料、汽车用品、运动健康用品、乐器、厨房用品、艺术收藏品等共十六大类，千万种单品，目前，再谈和已经签约的合作商已超过 500多家，遍布全球多个国家和地区，产品单品正在陆续筛选、评估和上线之中。', '关注环球臻品网的朋友们：<br />\r\n大家好！<br />\r\n环球臻品成立于2014年，目前是中国电商中具有最新经营理念和创智模式的大型网上交易平台。<br />\r\n<br />\r\n环球臻品网站是针对国内高人气网络消费人群创立的高质量、大品牌、上档次的综合型产品购物网站。销售的产品包括：服装鞋帽、礼品箱包、家具家装、办公\r\n数码、电器珠宝、化妆品、食品饮料、汽车用品、运动健康用品、乐器、厨房用品、艺术收藏品等共十六大类，千万种单品，目前，再谈和已经签约的合作商已超过\r\n500多家，遍布全球多个国家和地区，产品单品正在陆续筛选、评估和上线之中。<br />\r\n网站特别推出的海外臻品板块，将把香港、台湾、韩国、巴西、欧美等国家和地区消费者难得一见的商品呈现在网站柜台，使您足不出户，便可畅购海外。<br />\r\n2014年建设至今，短短的一年时间，一个年轻的互联网创业团队何以如此受到商家的青睐？<br />\r\n网站创始人、致臻致圣（北京）投资管理有限公司董事长温继华先生在接受《中国改革报》记者专访时指出：“环球臻品网”本质上是大型开放式营销渠道电商平\r\n台，融汇循环消费、立体产业和创智金融理念，开创针对消费者的‘极致服务消费模式’。吃法：什么健康吃什么，穿法：怎么漂亮怎么穿，用法：什么时尚用什\r\n么，玩法：哪里好玩去哪里，活法：怎么高兴怎么来”。他希望通过强化消费者的购买能力来为生产企业创造巨大的成长空间。搭建“环球臻品”大型开放式电商平\r\n台，将创造出数以万计的创业机会，为无数怀揣希望的创业者点亮梦想。<br />\r\n环球臻品自成立以来，始终专注于经营模式的研发和改进，不断深化各项服务，切实让环球臻品网成为生产者、创业者和消费者之间的一个共融共赢的平台；自从消\r\n费者踏进环球臻品网那一刻起，便成为这个网站大家庭中的一员，在网站消费的每一次，在不知不觉中，已经神奇地为自己播种了财富，这个财富还会在后来的消费\r\n中继续成长。而播撒这个财富的便是环球臻品经营理念下的电商“消费增值”服务模式。即入驻商家以相同于其他同类销售渠道零售价格出售商品，消费者在环球臻\r\n品网站每一笔消费均能得到极致的消费增值。<br />\r\n企业是中国消费者基金会3.15全国防伪打假联盟单位。环球臻品网的所有入驻商家必须经过环球臻品团队严格审查，并签署质量保证协议，在产品质量上接受网站和消费者的全面监督。<br />\r\n物美价廉的产品不断吸引着高品质的消费人群，不断成长的商家发展规模越来越大，一个成功的商业平台让更多的人不断成功，这就是环球臻品最初的目标。<br />\r\n环球臻品的经营理念来自企业领导人对法国“弥芮萨”品牌历史的发掘，2014年不断成熟的模式吸引着越来越多的商家关注这一新型网络平台。 \r\n目前，环球臻品在全国范围内快速铺设环球臻品的加盟服务机构，让环球臻品网近距离服务各地在线企业和消费者，加盟服务商扩充数量将在2015年年内创造上\r\n万个卓越的创业机会。<br />\r\n<br />\r\n环球臻品网的诞生，开创了先进的网购消费理念，将使入驻商家在这个平台上享受环球臻品网络销售平台提供的免费推广和指导，销售额迅速增长；因为合理的价格\r\n和过硬的保真产品，消费者的购买力将进一步加强，消费财富如同雪球般地滚动起来。今年，中国互联网创新热潮将进一步释放，我国网络交易额有望突破2万亿大\r\n关，环球臻品网如一缕春风，助力的中国电商市场更加生机勃勃！<br />\r\n<div style="text-align:right;">\r\n	<span style="line-height:1.5;"> 环球臻品网 </span> \r\n</div>\r\n<div style="text-align:right;">\r\n	<span style="line-height:1.5;"> 2014年10月31日 </span> \r\n</div>', 'uploads/image/20150619/1434702778.png', '', 70, 24, 1434636240, 'true', '', 0),
